SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

declare @object_name varchar(255);
set @object_name = (case when '$perfmon_service_name' = 'MSSQLSERVER' then 'SQLServer' else '$perfmon_service_name' end);
select pc.collection_time_utc as time, 
	counter as metric
	--,instance,
	,pc.value
from $perfmon_table_name pc with (nolock)
where $__timeFilter(collection_time_utc) and pc.host_name = '$perfmon_host_name' and
	  pc.object = (@object_name+':sql statistics') and pc.counter in ('Batch Requests/sec')
order by time

