declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255) = '$server';
declare @start_time_utc datetime2;
declare @end_time_utc datetime2;
declare @delta_days_threshold int = ${delta_days_threshold};
declare @top_x_filter int = ${top_x_filter};
declare @database varchar(255) = '$database';

set @database = case when ltrim(rtrim(@database)) = '__All__' then null else @database end;
set @start_time_utc = $__timeFrom();
set @end_time_utc = $__timeTo();
set @params = N'@database varchar(255), @delta_days_threshold int, @top_x_filter int';

set quoted_identifier off;
set @sql = "/* SQLMonitor - BlitzIndex Analysis - 0 Delta Rows */
set nocount on;

if object_id('tempdb..#delta_tables') is not null
	drop table #delta_tables;
select top (@top_x_filter) --*
		[database_name], [schema_name], [table_name]
		--,total_rows_max = max(total_rows)
		--,total_rows_min = min(total_rows)
		--,total_rows_avg = avg(total_rows)
		--,days_count = datediff(day,min(run_datetime),max(run_datetime))
		,total_reserved_GB = max(convert(numeric(20,2),bi.total_reserved_MB/1024.0))
into #delta_tables
from ${blitzindex_table_name} bi
where bi.run_datetime >= dateadd(day,-90,getdate())
and index_id <= 1
"+(case when @database is null then '-- ' else '' end)+"and bi.database_name = @database
group by [database_name], [schema_name], [table_name]
having min(total_rows) = max(total_rows) and max(total_rows) = min(total_rows)
	and datediff(day,min(run_datetime),max(run_datetime)) > @delta_days_threshold
order by total_reserved_GB desc;

select 	--collection_time = DATEADD(mi, DATEDIFF(mi, getdate(), getutcdate()), bi.run_datetime),
				bi.database_name, [object_name] = quotename(bi.schema_name)+'.'+quotename(bi.table_name), 
				/* bi.index_name, bi.object_type, */ 
				bi.index_size_summary, bi.index_definition,
				bi.index_usage_summary, bi.index_op_stats, --bi.total_rows, bi.total_reserved_MB, bi.total_reserved_LOB_MB,
				bi.data_compression_desc, bi.create_date, bi.more_info
from ${blitzindex_table_name} bi
inner join #delta_tables dt
	on bi.run_datetime = (select max(i.run_datetime) from ${blitzindex_table_name} i)
	and bi.database_name = dt.database_name and bi.schema_name = dt.schema_name and bi.table_name = dt.table_name
	and bi.index_id <= 1
order by bi.total_reserved_MB desc, bi.total_rows desc
"
set quoted_identifier on;

if ($is_local = 1)
  exec dbo.sp_executesql @sql, @params, @database, @delta_days_threshold, @top_x_filter;
else
  exec [$server].[$dba_db].dbo.sp_executesql @sql, @params, @database, @delta_days_threshold, @top_x_filter;

